    def swg(self,comp,mine):
        if (comp == mine):
                return None
        elif (comp == 's' and mine == 'g'):
                return True
        elif (comp == 'w' and mine == 's'):
                return True
        elif (comp == 'g' and mine == 'w'):
                return True
        else:
                return False



    def checkResult(self):
        if (self.l1.text() =='s' or self.l1.text() =='w' or self.l1.text() =='g') and (self.l4.text() =='s' or self.l4.text() =='w' or self.l4.text() =='g'):
                mine = self.l1.text()
                comp = self.l4.text()
                self.l2.setText(mine)
                self.l3.setText(comp)
                win = self.swg(comp,mine)
                if win:
                        self.result_L.setText("HERO WIN")
                elif win is None:
                        self.result_L.setText("MATCH DRAW")
                else:
                        self.result_L.setText("ENEMY WIN")

        else:
                msg ="Please enter valid later."
                self.shwdlg(msg)
        self.l1.setText('')
        self.l4.setText('')

